INSERT INTO `fa_admin_log` VALUES (422, 1, 'admin', '/FaElegance/public/ZUjGpgkWDP.php/bookshelf/bookbank/booklist/edit/ids/1?dialog=1', '上架书籍 / 书籍管理 / 上架列表 / 编辑', '{\"dialog\":\"1\",\"row\":{\"content\":\"《琵琶行》是唐代诗人白居易创作的长篇叙事诗。此诗通过对歌妓琵琶女高超弹奏技艺和不幸人生经历的描述，揭露了封建社会官僚腐败、民生凋敝、人才埋没等不合理现象，表达了诗人对琵琶女的深切同情，也抒发了诗人对自己无辜被贬的愤懑之情。全诗结构严谨，错落有致，情节曲折，波澜起伏；叙事与抒情紧密结合，塑造出完整鲜明的人物形象；语言流转匀称，优美和谐，特别是描绘琵琶的演奏，比喻贴切，化虚为实，呈现出鲜明的音乐形象。\",\"book_name\":\"琵琶行\",\"book_image\":\"\\/uploads\\/20240429\\/39948298d22f1f36e924fdb841ffad17.jpg\",\"book_file\":\"\\/uploads\\/20240325\\/4bd7ec31591064a1c8913b5905c8be4e.pdf\",\"time_shelf\":\"15:33:17\",\"sound_file\":\"\\/uploads\\/20240327\\/ca57a8328e79479a878eb6a3ed7253cf.mp3\"},\"ids\":\"1\"}', '222.249.182.5', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/123.0.0.0 Safari/537.36', 1714361679);
