INSERT INTO `fa_admin_log` VALUES (438, 1, 'admin', '/FaElegance/public/ZUjGpgkWDP.php/bookshelf/bookbank/booklist/edit/ids/2?dialog=1', '上架书籍 / 书籍管理 / 上架列表 / 编辑', '{\"dialog\":\"1\",\"row\":{\"content\":\"《将进酒》是唐代著名浪漫主义诗人李白沿用乐府古题创作的一首七言歌行。此诗思想内容非常深沉，艺术表现非常成熟，在同题作品中影响最大。诗人豪饮高歌，借酒消愁，抒发了忧愤深广的人生感慨。诗中交织着失望与自信、悲愤与抗争的情怀，体现出诗人强烈的豪纵狂放的个性。\",\"book_name\":\"将进酒\",\"book_image\":\"\\/uploads\\/20240429\\/34510851a828bafd3a54170c636fd128.jpg\",\"book_file\":\"\\/uploads\\/20240325\\/a45621b0cb68c09d8c1fa681dded8342.pdf\",\"time_shelf\":\"15:35:05\",\"sound_file\":\"\\/uploads\\/20240326\\/bd1920d6d23f48c9638da733d0aab199.mp3\"},\"ids\":\"2\"}', '222.249.182.5', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/123.0.0.0 Safari/537.36', 1714362026);
INSERT INTO `fa_admin_log` VALUES (439, 1, 'admin', '/FaElegance/public/ZUjGpgkWDP.php/bookshelf/bookbank/booklist/edit/ids/3?dialog=1', '上架书籍 / 书籍管理 / 上架列表 / 编辑', '{\"dialog\":\"1\",\"row\":{\"content\":\"《短歌行》是三国时期魏文帝曹丕创作的一首乐府诗。此诗写思亲之情。每四句为一解，共六解。第一解写睹物思人，而人不在；第二解写失去依恃，泪流不已；第三解以写鹿麂飞鸟之乐，反衬己悲；第四解写孤独无依之哀；第五解写怀忧早衰；末解痛亲早亡。全诗写得质朴、本色，且其中有作者诗文“工于言情”的特点，十分细致、生动。\",\"book_name\":\"短歌行\",\"book_image\":\"\\/uploads\\/20240429\\/8bdc90d360cae9ce449d211cb6307169.jpg\",\"book_file\":\"\\/uploads\\/20240328\\/5bd01ef96bdeb6f899dfb8ce4d047996.pdf\",\"time_shelf\":\"17:10:35\",\"sound_file\":\"\\/uploads\\/20240328\\/12.MP3\"},\"ids\":\"3\"}', '222.249.182.5', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/123.0.0.0 Safari/537.36', 1714362052);
