INSERT INTO `fa_admin_log` VALUES (745, 2, 'test', '/FaElegance/public/ZUjGpgkWDP.php/bookshelf/discover/activitymodel/edit', '上架书籍 / 发现 / 活动页 / 编辑', '{\"row\":{\"loginImage\":\"\\/uploads\\/20240517\\/741b260097b1265c81b8f19d99d12f7e.png\",\"activityTitle\":\"小满\",\"activityIcon\":\"\\/uploads\\/20240517\\/8f96fc30dbfe89842a4988a0429fe9dd.png\",\"activityText\":\"物有小满，始得成熟；人有小满，才得尽然。小满，二十四节气中的第八个节气，也是夏季的第二个节气。\\r\\n小满期间南方的暴雨开始增多，降水频繁；民谚云：小满小满，江河渐满。小满中的&quot;满&quot;，指雨水之盈。\",\"activityImage\":\"\\/uploads\\/20240517\\/741b260097b1265c81b8f19d99d12f7e.png\",\"activityParagraph\":\"由于中国南北方气候和物候的差异，小麦饱满、成熟及收割时间也会有所差异。\\r\\n一般而言，中国南方一些地区的小麦在立夏、小满时节已开始饱满成熟收割。中国北方一些地区的小麦在芒种时节才开始成熟收割，小满时节北方地区的小麦作物进入灌浆期，籽粒渐渐饱满。\",\"soundTitle\":\"本期推荐：\"}}', '111.197.250.233', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/124.0.0.0 Safari/537.36', 1716453927);
INSERT INTO `fa_admin_log` VALUES (746, 2, 'test', '/FaElegance/public/ZUjGpgkWDP.php/bookshelf/discover/activitymodel/edit', '上架书籍 / 发现 / 活动页 / 编辑', '{\"row\":{\"loginImage\":\"\\/uploads\\/20240517\\/741b260097b1265c81b8f19d99d12f7e.png\",\"activityTitle\":\"小满\",\"activityIcon\":\"\\/uploads\\/20240517\\/8f96fc30dbfe89842a4988a0429fe9dd.png\",\"activityText\":\"物有小满，始得成熟；人有小满，才得尽然。小满，二十四节气中的第八个节气，也是夏季的第二个节气。\\r\\n小满期间南方的暴雨开始增多，降水频繁；民谚云：小满小满，江河渐满。小满中的&quot;满&quot;，指雨水之盈。\",\"activityImage\":\"\\/uploads\\/20240517\\/741b260097b1265c81b8f19d99d12f7e.png\",\"activityParagraph\":\"由于中国南北方气候和物候的差异，小麦饱满、成熟及收割时间也会有所差异。\\r\\n一般而言，中国南方一些地区的小麦在立夏、小满时节已开始饱满成熟收割。中国北方一些地区的小麦在芒种时节才开始成熟收割，小满时节北方地区的小麦作物进入灌浆期，籽粒渐渐饱满。\",\"soundTitle\":\"本期推荐：\"}}', '111.197.250.233', 'Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/124.0.0.0 Safari/537.36', 1716453947);
