INSERT INTO `fa_list_books` VALUES (8, '主题：小满', '五绝·小满', '/uploads/20240513/482442c368b15662876804263f184bbf.mp3', '/uploads/pdf/五绝·小满.pdf', '/uploads/20240429/769ee2e7ae628214ad2e79d10923e906.jpg', '上架', '唐朝', '李白', '<p>夜莺啼绿柳，皓月醒长空。</p>\r\n<p>最爱垄头麦，迎风笑落红。</p>');
INSERT INTO `fa_list_books` VALUES (50, '主题：小满', '咏廿四气诗·小满四月中', '/uploads/20240514/c13aa4d70ed5c3d6dc738a84bd69d3a4.mp3', '/uploads/pdf/咏廿四气诗·小满四月中.pdf', '/uploads/20240507/769ee2e7ae628214ad2e79d10923e906.jpg', '上架', '唐朝', '李白', '<p>小满气全时，如何靡草衰。</p>\r\n<p>田家私黍稷，方伯问蚕丝。</p>\r\n<p>杏麦修镰钐，錋欔竖棘篱。</p>\r\n<p>向来看苦菜，独秀也何为？</p>');
INSERT INTO `fa_list_books` VALUES (51, '主题：石榴花', '题榴花', '/uploads/20240514/2c81bc09f85ca9ba0430146a46321a03.mp3', '/uploads/pdf/题榴花.pdf', '/uploads/20240521/da3c995bc2e1ae16a1dc4d1db363084e.png', '上架', '唐朝', '李白', '<p>五月榴花照眼明，枝间时见子初成。</p>\r\n<p>可怜此地无车马，颠倒青苔落绛英。</p>');
INSERT INTO `fa_list_books` VALUES (52, '主题：石榴花', '咏邻女东窗海石榴', '/uploads/20240514/878c83d16f55be6029dafc68e6fcc197.mp3', '/uploads/pdf/咏邻女东窗海石榴.pdf', '/uploads/20240521/94be8429093a40114d3a38497bdf8523.png', '上架', '唐朝', '李白', '<p>鲁女东窗下，海榴世所稀。</p>\r\n<p>珊瑚映绿水，未足比光辉。</p>\r\n<p>清香随风发，落日好鸟归。</p>\r\n<p>愿为东南枝，低举拂罗衣。</p>\r\n<p>无由一攀折，引领望金扉。</p>');
INSERT INTO `fa_list_books` VALUES (53, '主题：枇杷', '赴嘉州过城固县寻永安超禅师房', '/uploads/20240514/d02c82113508bac7fd5497dfe29243b2.mp3', '/uploads/pdf/赴嘉州过城固县寻永安超禅师房.pdf', '/uploads/20240520/9350b4d5d8d73bf7560cd9281ba3dd2e.png', '未上架', '唐朝', '岑参 ', '<p>满寺枇杷冬著花，老僧相见具袈裟。</p>\r\n<p>汉王城北雪初霁，韩信台西日欲斜。</p>\r\n<p>门外不须催五马，林中且听演三车。</p>\r\n<p>岂料巴川多胜事，为君书此报京华。</p>');
INSERT INTO `fa_list_books` VALUES (54, '主题：枇杷', '寄蜀中薛涛校书', '/uploads/20240514/b07da7237621aa167b54865d42872e69.mp3', '/uploads/pdf/寄蜀中薛涛校书.pdf', '/uploads/20240521/94be8429093a40114d3a38497bdf8523.png', '未上架', '唐朝', '王建', '<p>万里桥边女校书，枇杷花里闭门居。</p>\r\n<p>扫眉才子知多少，管领春风总不如。</p>');
